	Run code in a sandbox with only the specified context variables in scope
	*/
	$tw.utils.evalSandboxed = $tw.browser ? $tw.utils.evalGlobal : function(code,context,filename,allowGlobals) {
		return $tw.utils.evalGlobal(
			code,context,filename,
			allowGlobals ? vm.createContext({}) : $tw.utils.sandbox,
			allowGlobals
		);
	};

	/*
	Creates a PasswordPrompt object
	*/
	$tw.utils.PasswordPrompt = function() {
		// Store of pending password prompts
		this.passwordPrompts = [];
		// Create the wrapper
		this.promptWrapper = $tw.utils.domMaker("div",{"class":"tc-password-wrapper"});
		document.body.appendChild(this.promptWrapper);
		// Hide the empty wrapper
		this.setWrapperDisplay();
	};

	/*
	Hides or shows the wrapper depending on whether there are any outstanding prompts
	*/
	$tw.utils.PasswordPrompt.prototype.setWrapperDisplay = function() {
		if(this.passwordPrompts.length) {
			this.promptWrapper.style.display = "block";
		} else {
			this.promptWrapper.style.display = "none";
		}
	};

	/*
	Adds a new password prompt. Options are:
	submitText: text to use for submit button (defaults to "Login")
	serviceName: text of the human readable service name
	noUserName: set true to disable username prompt
	canCancel: set true to enable a cancel button (callback called with null)
	repeatPassword: set true to prompt for the password twice
	callback: function to be called on submission with parameter of object {username:,password:}. Callback must return `true` to remove the password prompt
	*/
	$tw.utils.PasswordPrompt.prototype.createPrompt = function(options) {
		// Create and add the prompt to the DOM
		var self = this,
			submitText = options.submitText || "Login",
			dm = $tw.utils.domMaker,
			children = [dm("h1",{text: options.serviceName})];
		if(!options.noUserName) {
			children.push(dm("input",{
				attributes: {type: "text", name: "username", placeholder: $tw.language.getString("Encryption/Username")}
			}));
		}
		children.push(dm("input",{
			attributes: {
				type: "password",
				name: "password",
				placeholder: ( $tw.language == undefined ? "Password" : $tw.language.getString("Encryption/Password") )
			}
		}));
		if(options.repeatPassword) {
			children.push(dm("input",{
				attributes: {
					type: "password",
					name: "password2",
					placeholder: $tw.language.getString("Encryption/RepeatPassword")
				}
			}));
		}
		if(options.canCancel) {
			children.push(dm("button",{
				text: $tw.language.getString("Encryption/Cancel"),
				attributes: {
					type: "button"
				},
				eventListeners: [{
						name: "click",
						handlerFunction: function(event) {
							self.removePrompt(promptInfo);
							options.callback(null);
						}
					}]
			}));
		}
		children.push(dm("button",{
			attributes: {type: "submit"},
			text: submitText
		}));
		var form = dm("form",{
			attributes: {autocomplete: "off"},
			children: children
		});
		this.promptWrapper.appendChild(form);
		window.setTimeout(function() {
			form.elements[0].focus();
		},10);
		// Add a submit event handler
		var self = this;
		form.addEventListener("submit",function(event) {
			// Collect the form data
			var data = {},t;
			$tw.utils.each(form.elements,function(element) {
				if(element.name && element.value) {
					data[element.name] = element.value;
				}
			});
			// Check that the passwords match
			if(options.repeatPassword && data.password !== data.password2) {
				alert($tw.language.getString("Encryption/PasswordNoMatch"));
			} else {
				// Call the callback
				if(options.callback(data)) {
					// Remove the prompt if the callback returned true
					self.removePrompt(promptInfo);
				} else {
					// Clear the password if the callback returned false
					$tw.utils.each(form.elements,function(element) {
						if(element.name === "password" || element.name === "password2") {
							element.value = "";
						}
					});
				}
			}
			event.preventDefault();
			return false;
		},true);
		// Add the prompt to the list
		var promptInfo = {
			serviceName: options.serviceName,
			callback: options.callback,
			form: form,
			owner: this
		};
		this.passwordPrompts.push(promptInfo);
		// Make sure the wrapper is displayed
		this.setWrapperDisplay();
		return promptInfo;
	};

	$tw.utils.PasswordPrompt.prototype.removePrompt = function(promptInfo) {
		var i = this.passwordPrompts.indexOf(promptInfo);
		if(i !== -1) {
			this.passwordPrompts.splice(i,1);
			promptInfo.form.parentNode.removeChild(promptInfo.form);
			this.setWrapperDisplay();
		}
	}

	/*
	Crypto helper object for encrypted content. It maintains the password text in a closure, and provides methods to change
	the password, and to encrypt/decrypt a block of text
	*/
	$tw.utils.Crypto = function() {
		var sjcl = $tw.node ? (global.sjcl || require("./sjcl.js")) : window.sjcl,
			currentPassword = null,
			callSjcl = function(method,inputText,password) {
				password = password || currentPassword;
				var outputText;
				try {
					if(password) {
						outputText = sjcl[method](password,inputText);
					}
				} catch(ex) {
					console.log("Crypto error:" + ex);
					outputText = null;
				}
				return outputText;
			};
		$tw.sjcl = sjcl;
		this.setPassword = function(newPassword) {
			currentPassword = newPassword;
			this.updateCryptoStateTiddler();
		};
		this.updateCryptoStateTiddler = function() {
			if($tw.wiki) {
				var state = currentPassword ? "yes" : "no",
					tiddler = $tw.wiki.getTiddler("$:/isEncrypted");
				if(!tiddler || tiddler.fields.text !== state) {
					$tw.wiki.addTiddler(new $tw.Tiddler({title: "$:/isEncrypted", text: state}));
				}
			}
		};
		this.hasPassword = function() {
			return !!currentPassword;
		}
		this.encrypt = function(text,password) {
			return callSjcl("encrypt",text,password);
		};
		this.decrypt = function(text,password) {
			return callSjcl("decrypt",text,password);
		};
	};

	$tw.utils.CSE = function () {
		var currentPassword = null;
		this.setPassword = function(newPassword) {
			currentPassword = newPassword;
			if($tw.CSE.launched){
				var isRemembered = $tw.wiki.getTiddlerData("$:/plugins/FSpark/TW5-CSE/metaconfig.json")
				if(isRemembered && isRemembered["RmbPwd"]==="yes"){
					this.rememberPassword();
				}
			}
			this.updateCryptoStateTiddler();
		};
		this.rememberPassword = function (){
			if(window && window.localStorage){
				window.localStorage.setItem("tw5-cse-pwd", currentPassword);
			}
		};
		this.forgetPassword =  function (){
			if(window && window.localStorage){
				window.localStorage.removeItem("tw5-cse-pwd");
			}
		};
		this.updateCryptoStateTiddler = function() {
			if($tw.wiki) {
				var state = currentPassword ? "yes" : "no",
					tiddler = $tw.wiki.getTiddler("$:/isCSEncrypted");
				if(!tiddler || tiddler.fields.text !== state) {
					$tw.wiki.addTiddler(new $tw.Tiddler({title: "$:/isCSEncrypted", text: state}));
				}
			}
		};
		this.forcePush = function (filter, widget) {
			debugger;
			filter = filter || $tw.wiki.getTiddlerText('$:/config/TW5-CSE/EncryptFilter',"[all[]!is[system]]")

			widget = widget || $tw.rootWidget

			var filterTiddlers = $tw.wiki.filterTiddlers(filter, widget)
			var filterTiddlersLength = filterTiddlers.length

			filterTiddlers.forEach(function (title)  {
				if($tw.utils.hop($tw.wiki.changeCount, title)) {
					$tw.wiki.changeCount[title]++;
				} else {
					$tw.wiki.changeCount[title] = 1;
				}
			})

			var id = $tw.wiki.getTiddlerText('$:/temp/CSE-IntervalID')
			if(id) clearTimeout(parseInt(id))
			var self = this;
			$tw.modal.display("$:/plugins/FSpark/TW5-CSE/ui/PushingModal")
			var startTime = Date.now();
			id = setInterval(function() {
				if($tw.syncer.isDirty()){
					// Filter out unsynced
					filterTiddlers = filterTiddlers.filter(function (title) {
						return !$tw.syncer.tiddlerInfo[title] || $tw.wiki.getChangeCount(title) > $tw.syncer.tiddlerInfo[title].changeCount
					})
					var syncedTiddlers = filterTiddlersLength - filterTiddlers.length
					var percentComplete = (syncedTiddlers * 100 / filterTiddlersLength).toFixed(2)  + '%'

					var endTime = Date.now(); 
					var timeElapsed = (endTime - startTime) / 1000;
					var syncSpeed = syncedTiddlers / timeElapsed;
					var remainingTiddlersSize = filterTiddlers.length; 
					var remainingTime = remainingTiddlersSize / syncSpeed;

					var hours = Math.floor(remainingTime / 3600).toString().padStart(2, '0');
					var minutes = Math.floor((remainingTime % 3600) / 60).toString().padStart(2, '0');
					var seconds = Math.floor(remainingTime % 60).toString().padStart(2, '0');

					$tw.wiki.addTiddler({
						title: "$:/temp/CSENumTasksInProgress",
						text: `${syncedTiddlers}/${filterTiddlersLength} ${percentComplete}` })
					$tw.wiki.addTiddler({
						title: "$:/temp/CSESyncEstimatedTimeLeft",
						text: `Estimated time left: ${hours}:${minutes}:${seconds}` })
				}else{
					// debugger;
					$tw.wiki.addTiddler({title: "$:/state/cse-modal-close", text: "yes"})
					clearTimeout(id)
					$tw.wiki.deleteTiddler("$:/temp/CSE-IntervalID")
					$tw.wiki.deleteTiddler("$:/temp/CSENumTasksInProgress")
				}
			}, 500);
			$tw.wiki.addTiddler({title: "$:/temp/CSE-IntervalID",text: id.toString()})
		}
		this.saveTiddler = function (tiddler, fields) {
			debugger;
			$tw.wiki.addTiddler(
				new $tw.Tiddler(
					// $tw.wiki.getModificationFields(),
					tiddler,
					this.clearNonStandardFields(tiddler),
					fields
				)
			);
		};
		this.encryptFields = function (title, password) {
			password = password || currentPassword;
			var jsonData = $tw.wiki.getTiddlerAsJson(title);
			return $tw.crypto.encrypt(jsonData, password);
		};

		this.decryptFields = function (fields, password) {
			password = password || currentPassword;
			var JSONfields = $tw.crypto.decrypt(fields.encrypted, password);
			if(!!JSONfields) {
				return JSON.parse(JSONfields);
			}
			console.log(
				"Error decrypting " + fields.title + ". Probably bad password"
			);
			return false;
		};
		this.clearNonStandardFields = function (tiddler) {
			var standardFieldNames =
				"title tags modified modifier created creator".split(" ");
			var clearFields = {};
			for(var fieldName in tiddler.fields) {
				if(standardFieldNames.indexOf(fieldName) === -1) {
					clearFields[fieldName] = undefined;
				}
			}
			console.log("Cleared fields " + JSON.stringify(clearFields));
			return clearFields;
		};
	}
	/////////////////////////// Module mechanism

	/*
	Execute the module named 'moduleName'. The name can optionally be relative to the module named 'moduleRoot'
	*/
	$tw.modules.execute = function(moduleName,moduleRoot) {
		var name = moduleName;
		if(moduleName.charAt(0) === ".") {
			name = $tw.utils.resolvePath(moduleName,moduleRoot)
		}
		if(!$tw.modules.titles[name]) {
			if($tw.modules.titles[name + ".js"]) {
				name = name + ".js";
			} else if($tw.modules.titles[name + "/index.js"]) {
				name = name + "/index.js";
			} else if($tw.modules.titles[moduleName]) {
				name = moduleName;
			} else if($tw.modules.titles[moduleName + ".js"]) {
				name = moduleName + ".js";
			} else if($tw.modules.titles[moduleName + "/index.js"]) {
				name = moduleName + "/index.js";
			}
		}
		var moduleInfo = $tw.modules.titles[name],
			tiddler = $tw.wiki.getTiddler(name),
			_exports = {},
			sandbox = {
				module: {exports: _exports},
				//moduleInfo: moduleInfo,
				exports: _exports,
				console: console,
				setInterval: setInterval,
				clearInterval: clearInterval,
				setTimeout: setTimeout,
				clearTimeout: clearTimeout,
				Buffer: $tw.browser ? undefined : Buffer,
				$tw: $tw,
				require: function(title) {
					return $tw.modules.execute(title, name);
				}
			};

		Object.defineProperty(sandbox.module, "id", {
			value: name,
			writable: false,
			enumerable: true,
			configurable: false
		});

		if(!$tw.browser) {
			$tw.utils.extend(sandbox,{
				process: process
			});
		} else {
			/*
			CommonJS optional require.main property:
				In a browser we offer a fake main module which points back to the boot function
				(Theoretically, this may allow TW to eventually load itself as a module in the browser)
			*/
			Object.defineProperty(sandbox.require, "main", {
				value: (typeof(require) !== "undefined") ? require.main : {TiddlyWiki: _boot},
				writable: false,
				enumerable: true,
				configurable: false
			});
		}
		if(!moduleInfo) {
			// We could not find the module on this path
			// Try to defer to browserify etc, or node
			var deferredModule;
			if($tw.browser) {
				if(window.require) {
					try {
						return window.require(moduleName);
					} catch(e) {}
				}
				throw "Cannot find module named '" + moduleName + "' required by module '" + moduleRoot + "', resolved to " + name;
