	$tw.boot.loadStartup = function(options){

		// Load tiddlers
		if($tw.boot.tasks.readBrowserTiddlers) {
			$tw.loadTiddlersBrowser();
		} else {
			$tw.loadTiddlersNode();
		}
		// Load any preloaded tiddlers
		if($tw.preloadTiddlers) {
			$tw.wiki.addTiddlers($tw.preloadTiddlers);
		}
